export default {
  props: ['meetup'],
  data: function data() {
    return {
      editDialog: false,
      editableTime: null
    };
  },
  methods: {
    onSaveChanges: function onSaveChanges() {
      this.editDialog = false;
      var newDate = new Date(this.meetup.date);
      var hours = this.editableTime.match(/^(\d+)/)[1];
      var minutes = this.editableTime.match(/:(\d+)/)[1];
      newDate.setHours(hours);
      newDate.setMinutes(minutes);
      this.$store.dispatch('updateMeetup', {
        id: this.meetup.id,
        date: newDate
      });
    }
  },
  created: function created() {
    this.editableTime = new Date(this.meetup.date).toTimeString().substr(0, 8);
  }
};
