export default {
  props: ['meetup'],
  data: function data() {
    return {
      editDialog: false,
      editableDate: null
    };
  },
  methods: {
    onSaveChanges: function onSaveChanges() {
      this.editDialog = false;
      var newDate = new Date(this.meetup.date);
      var newDay = new Date(this.editableDate).getUTCDate();
      var newMonth = new Date(this.editableDate).getUTCMonth();
      var newYear = new Date(this.editableDate).getUTCFullYear();
      newDate.setUTCDate(newDay);
      newDate.setUTCMonth(newMonth);
      newDate.setUTCFullYear(newYear);
      this.$store.dispatch('updateMeetup', {
        id: this.meetup.id,
        date: newDate
      });
    }
  },
  created: function created() {
    this.editableDate = new Date(this.meetup.date).toISOString().substr(0, 10);
  }
};
